<?php if (! defined('BASEPATH'))exit('No direct script access allowed');class Login_admin extends CI_Controller {		function __construct(){		parent::__construct();		$this->load->model("login_admin_model");	}		public function index()	{		$data["title"]="Question";		//$data["page"]="question";		$this->load->view('login_admin',$data);	}		function login_check(){		if($this->input->post()){						$post=$this->input->post();						$check=$this->login_admin_model->login_check($post);			$conn=$this->login_admin_model->get_config();			$num=$check->num_rows();						//echo $num;			if($num==1){												//$this->session->sess_destroy();								$ck=$check->row_array();								$ck["admin_password"]="";				$ck["logged_in"]=true;								$this->session->set_userdata($ck);												$co=$conn->row_array();								$this->session->set_userdata($co);								//print_r("<pre>");				//print_r($this->session->all_userdata());				//print_r("</pre>");							redirect("admin");																		}else{				$con=$conn->result_array();								if($post["username"]==$con[0]["username"]){					if(md5($post["password"])==$con[0]["password"]){						$co=$conn->row_array();										$this->session->set_userdata($co);						redirect("config");					}					else{						//echo "here 1";						redirect("login_admin");						}				}				//echo "here2";				$this->session->set_flashdata('error','invalid password or username');				redirect("login_admin");						}											}else{			//echo "here2";			redirect("login_admin");				}	}		function logout(){		$this->session->sess_destroy();				redirect("login_admin");	}}?>